require(['jquery'], function ($) {
'use strict';
$.noConflict();
$(document).ready(function(){
    $('.price-wrapper').removeAttr('data-price-amount');
    setInterval(function(){
    $('.super-attribute-select').on('change', function(){

        var pr = $('.old-price .price').text();
        var reg = $('.price-final_price .price').text();


        var prr = pr.replace(/,/g , '');
        var regg = reg.replace(/,/g , '');


        var rpe = (regg.toString().split(".")[0]);
        var pe = (prr.toString().split(".")[0]);


        var min = pe.match(/(\d+)/);
        var rmin = rpe.match(/(\d+)/);


        var i = 0;
        var comp = (min[0] - rmin[0]) / min[0];
        var tot = comp * 100;
        console.log(Math.round(tot));
        $('.discount strong.dis_val').text(Math.round(tot)); 
    });
    }, 500);
});    
});

